css:
/*
	common style for whole page
	This should include:
	+ page size related settings
	+ font family settings
	+ line spacing settings
*/
.print-format div,
.print-format span,
.print-format td,
.print-format h1,
.print-format h2,
.print-format h3,
.print-format h4 {
	font-family: serif;
}
.print-heading {
	text-align: right;
	text-transform: uppercase;
	font-weight: bold;
	padding-bottom: 5px;
	margin-bottom: 10px;
	border-bottom: 2px solid #ccc;
}

.print-format table.table-bordered {
	border-top: 2px solid #ddd;
}

.print-format th{
	background-color: #eee !important;
}

.page-title .title-image {
    background-color: #eee !important;
}
[document-status] {
	margin-bottom: 0 !important;
	margin-top: 0 !important;

}

.container.page-body {
    overflow-y: hidden;
}
.container {
    width: 97% !important;
}

.signature-box{
	height: 80px;
	text-align:center;
}


img.stamp{
	display:inline-block;
	width:120px;

}

img.signature_stamp{
	display:inline-block;
	width:150px;
}



.page-break{
    padding:10px 0 !important;
}
.custom-page{
	padding:10px 0 !important;
}

h1 small,
h2 small,
h3 small,
h4 small,
h5 small,
h6 small,
.h1 small,
.h2 small,
.h3 small,
.h4 small,
.h5 small,
.h6 small,
h1 .small,
h2 .small,
h3 .small,
h4 .small,
h5 .small,
h6 .small,
.h1 .small,
.h2 .small,
.h3 .small,
.h4 .small,
.h5 .small,
.h6 .small {
  font-weight: normal;
  line-height: 1;
  color: inherit !important;
}

h6.uppercase, .h6.uppercase {
	    font-size: 12px;
    font-weight: 500;
}


.form-section .section-head .collapse-indicator {
	color: #aaa;
}
.octicon, .mega-octicon {
    font: normal normal normal 18px/1 octicons;
}






.form-links .count{
	font-size:larger;
	font-weight:bold;
}

.letterhead-logo{
	display:inline-block;
    margin-right:10px;
}
.letterhead-title{
	display:inline-block;
    vertical-align:middle;
    font-size: 25px;
    width:75%;

}
.letterhead-address{
    text-align: center; 
    font-size: 12px;
}



.company_trn{
	display: inline-block;
    /* padding: 100px; */
    /* text-align: start; */
    width: 50%;
    text-align: left;
    font-weight: bold;
}
.heading_right{
	display: inline-block;
    /* padding: 100px; */
    /* text-align: start; */
    width: 50%;
    text-align: right;
}

.data-field .print_label {
    padding-right: 5px !important;
}

.listview-main-section .page-form .octicon-search
{
    display:none !important;
}
.listview-main-section .page-form
{
	padding-left:0px !important;
}

.tree-label {
    font-size: large;
}

.tree {
    font-size:large;
}
.control-label{
	font-weight:bold !important;
}

.link-btn{
	background:#fff;
}

.form-sidebar .sidebar-image-section .standard-image {
    font-size: 46px !important;
}
.sidebar-image-section
{
    margin:0 auto !important;
}

.custom-doc-title{
    font-size:larger;
    text-align:center;
}




.signature-hr{
	width:60%;
	border:0;
}
.print-format hr {
	margin:5px;
}

.list-item__content--indicator {
    flex: 0;
}

.list-item .avatar {
    display: none;
}

.page-title .title-image:hover {
    opacity: 0.5;
}

.page-title .title-image
{
    cursor:pointer;
    -webkit-transition: all 0.6s;
	padding :20px 0 !important;
}

.avatar-small
{
    margin-right:0px !important;
}

.standard-image
{
    color: #111;
}




.editable-title .editable-indicator {
    display: inline-block;
}
span.editable-indicator {
    display: none;
 }  
.editable-indicator i {
    font-size: medium;
}

.editable-title .title-text {
    background-color: yellow;
    border-radius: 5px;
    padding: 5px;
}

.navbar-header .octicon
{
	font-size:22px;
}

.btn-sm, .btn-group-sm > .btn
{
    padding:6px 12px;
}

.mrp_is_dirty
{
	background-color:orange;
    border-color:orange;
}

.section-box
{
	font-size:larger !important;
}

a.list-sidebar-link
{
	padding:15px;
	width:100%;
}

a.list-sidebar-link.active, a.list-sidebar-link:hover {
    background-color: #eee !important;
    border-radius: 0px !important;
	text-decoration:none !important;
}

.tags-placeholder {
    padding: 10px;
    background: #eee;
	font-size:12px;
}

.navbar-fixed-top {
    box-shadow: 0px -4px 5px 6px #ccc;
}

.page-head {
    box-shadow: 0px -4px 5px 6px #ccc;
}






.layout-side-section .overlay-rightbar {
    margin-top: 0 !important;
    position: fixed;
    background: white;
    top: 0;
    right: 0;
    transform: translateX(110%);
    z-index: 9999;
    box-shadow: 5px 0 25px 0px rgba(0, 0, 0, 0.3);
    height: 100%;
    min-width: 25%;
    max-width: 60%;
	/* padding: 20px !important; */
    display: block !important;
    transition: transform 170ms;
	font-size:larger;
	font-weight:bolder;
  }
  .layout-side-section .overlay-rightbar.opened {
    transform: translateX(0);
    overflow-y: auto;
  }
  .layout-side-section .overlay-rightbar .divider {
    height: 1px;
    background-color: #d8dfe5;
    opacity: 0.7;
  }
  .layout-side-section .overlay-rightbar li:not(.divider):not(.tagit-new):not(.module-sidebar-item):not(.list-sidebar-item) {
    padding: 10px 15px;
  }
  .layout-side-section .overlay-rightbar .modified-by,
  .layout-side-section .overlay-rightbar .created-by {
    margin: 0;
  }
  .layout-side-section .overlay-rightbar .badge {
    /* top: 9px; */
    /* right: 15px; */
  }
  .layout-side-section .overlay-rightbar .reports-dropdown,
  .layout-side-section .overlay-rightbar .kanban-dropdown {
    /* margin-top: 10px; */
    /* margin-bottom: -10px; */
  }
  .layout-side-section .overlay-rightbar .reports-dropdown li:not(.divider),
  .layout-side-section .overlay-rightbar .kanban-dropdown li:not(.divider) {
    /* padding: 12.5px 0 !important; */
  }
  .layout-side-section .overlay-rightbar .reports-dropdown li.divider,
  .layout-side-section .overlay-rightbar .kanban-dropdown li.divider {
    height: 0;
  }

.close-sidebar {
  position: fixed;
  top: 0;
  right: 0;
  opacity: 0;
  background: #000;
  z-index: 1041;
  height: 100%;
  width: 100%;
  transition: opacity .3s ease-in-out;
  display:none;
}

 .close-sidebar.opened {
     opacity: 0.3;
     display:block;
  }





.sub-heading {
	position:absolute !important;
	z-index:0;
	top:5px !important;
	width:100% !important;
	text-align:center;
	right:0 !important;
	margin: 0 !important;
}

body.no-breadcrumbs .navbar .navbar-home:before
{
    display:none !important;
}



#navbar-breadcrumbs > li > a:before {

    content: "\f105" !important;

}

@media (max-width: 767px) {
  .page-actions {
    position: relative !important;
    float: right;
    padding-right: 0px;
    padding-left:0px;
  }
  .page-title {
    position: relative !important;
    width: 50% !important;
  }
  .page-title h1 {
    padding-right: 0px !important;
  }
  .page-head .page-title h1 {
    font-size: 18px;
  }
}

body[data-route^="Form"] .page-title .title-area {
    flex-direction: row !important;
}

.link-field.ui-front {
 width: 100%;
}

.table-multiselect.form-control input {
width:100%
}


.sidebar-menu .list-link
{
    margin:0 !important;
}

.layout-side-section .sidebar-menu
{
    padding:0 !important;
}

.print-report-filters
{
    margin-right:5px;
}

.report-wrapper .dt-scrollable,
.datatable-wrapper .dt-scrollable{
    width: auto !important;
}


.search-dialog .modal-dialog
{
    width:85vw;
}

.form-inner-toolbar {
    padding: 0 !important;
}

.form-inner-toolbar .btn {
    margin: 1px !important;
    padding: 10px !important;
    border: 1px solid #ccc;
}

.navbar-form .awesomplete {
  width: 40vw;
}
