css:
/*
	common style for whole page
	This should include:
	+ page size related settings
	+ font family settings
	+ line spacing settings
*/
.print-format div,
.print-format span,
.print-format td,
.print-format h1,
.print-format h2,
.print-format h3,
.print-format h4 {
	font-family: serif;
}
.print-heading {
	text-align: right;
	text-transform: uppercase;
	font-weight: bold;
	padding-bottom: 5px;
	margin-bottom: 10px;
	border-bottom: 2px solid #ccc;
}

.print-format table.table-bordered {
	border-top: 2px solid #ddd;
}

.print-format th{
	background-color: #eee !important;
}


[document-status] {
	margin-bottom: 0 !important;
		margin-top: 0 !important;

}
.signature-box{
	height: 80px;
	text-align:center;
}


img.stamp{
	display:inline-block;
	width:120px;

}

img.signature_stamp{
	display:inline-block;
	width:150px;
}



.page-break{
padding:10px 0 !important;
}
.custom-page{
	padding:10px 0 !important;
}

h1 small,
h2 small,
h3 small,
h4 small,
h5 small,
h6 small,
.h1 small,
.h2 small,
.h3 small,
.h4 small,
.h5 small,
.h6 small,
h1 .small,
h2 .small,
h3 .small,
h4 .small,
h5 .small,
h6 .small,
.h1 .small,
.h2 .small,
.h3 .small,
.h4 .small,
.h5 .small,
.h6 .small {
  font-weight: normal;
  line-height: 1;
  color: inherit !important;
}

h6.uppercase, .h6.uppercase {
	    font-size: 12px;
    font-weight: 500;
}


.form-section .section-head .collapse-indicator {
