.container {
    width: 98% !important;
}

.layout-side-section .overlay-rightbar {
    margin-top: 0 !important;
    position: fixed;
    background: white;
    top: 0;
    right: 0;
    transform: translateX(110%);
    z-index: 9999;
    box-shadow: 5px 0 25px 0px rgba(0, 0, 0, 0.3);
    height: 100%;
    min-width: 25%;
    max-width: 60%;
	/* padding: 20px !important; */
    display: block !important;
    transition: transform 170ms;
	font-size:larger;
	font-weight:bolder;
  }
  .layout-side-section .overlay-rightbar.opened {
    transform: translateX(0);
    overflow-y: auto;
  }
  .layout-side-section .overlay-rightbar .divider {
    height: 1px;
    background-color: #d8dfe5;
    opacity: 0.7;
  }
  .layout-side-section .overlay-rightbar li:not(.divider):not(.tagit-new):not(.module-sidebar-item):not(.list-sidebar-item) {
    padding: 10px 15px;
  }
  .layout-side-section .overlay-rightbar .modified-by,
  .layout-side-section .overlay-rightbar .created-by {
    margin: 0;
  }
  .layout-side-section .overlay-rightbar .badge {
    /* top: 9px; */
    /* right: 15px; */
  }
  .layout-side-section .overlay-rightbar .reports-dropdown,
  .layout-side-section .overlay-rightbar .kanban-dropdown {
    /* margin-top: 10px; */
    /* margin-bottom: -10px; */
  }
  .layout-side-section .overlay-rightbar .reports-dropdown li:not(.divider),
  .layout-side-section .overlay-rightbar .kanban-dropdown li:not(.divider) {
    /* padding: 12.5px 0 !important; */
  }
  .layout-side-section .overlay-rightbar .reports-dropdown li.divider,
  .layout-side-section .overlay-rightbar .kanban-dropdown li.divider {
    height: 0;
  }

.close-sidebar {
  position: fixed;
  top: 0;
  right: 0;
  opacity: 0;
  background: #000;
  z-index: 1041;
  height: 100%;
  width: 100%;
  transition: opacity .3s ease-in-out;
  display:none;
}

 .close-sidebar.opened {
     opacity: 0.3;
     display:block;
  }

  .modules-container[data-v-c80d066a] {
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr)) !important;
    grid-auto-rows: minmax(62px, 1fr);
}


