::-webkit-scrollbar {
    -webkit-border-radius: 100px;
    background-color: rgba(0,0,0,0);
    width: 9px;
}
::-webkit-scrollbar {
    height: 16px;
    overflow: visible;
}
::-webkit-scrollbar-button {
    height: 0;
    width: 0;
}
::-webkit-scrollbar-corner {
    background: transparent;
}
::-webkit-scrollbar-thumb {
    border: 1px solid rgba(255,255,255,0.4);
}
::-webkit-scrollbar-thumb {
    -webkit-border-radius: 100px;
    background: rgba(0,0,0,0.2);
}
::-webkit-scrollbar-thumb {
    border: solid transparent;
    border-width: 1px 1px 1px 6px;
    min-height: 28px;
    padding: 100px 0 0;
    -webkit-box-shadow: inset 1px 1px 0 rgba(0,0,0,.1), inset 0 -1px 0 rgba(0,0,0,.07);
    box-shadow: inset 1px 1px 0 rgba(0,0,0,.1), inset 0 -1px 0 rgba(0,0,0,.07);
}
::-webkit-scrollbar-track {
    background-clip: padding-box;
    border: solid transparent;
    border-width: 0 0 0 4px;
}


.carousel-caption {
    position: absolute;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 10;
    /* padding-top: 20px; */
    /* padding-bottom: 20px; */
    color: #fff;
    text-align: left;
    width: 100%;
    padding: 10px;
    height:100%;
}


.blur {
    display: block;
    height: 100%;
    pointer-events: none;
    width: 100%;
    filter: blur(2px);
}

.headerbefore {
    background-color: rgba(33,33,33,0);
    display: block;
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 1;
}

.item.gallery-item {
    overflow: hidden;
    width: 25%;
    min-width:300px;
    display: inline-block;
    max-height: 200px;
    margin: 5px;
    vertical-align: middle;
    box-shadow: 0px 0px 5px 2px #999;
}


.carousel-caption h4 {
    margin: 0.5em;
    color: white;
    font-size: 74px;
    font-weight: bold;
    overflow-wrap: break-word;
}

@media (max-width: 767px)
{
.carousel-caption h4 {
    font-size: 40px;
}
}

.JesterBox div {
  visibility: hidden;
  position: fixed;
  top: 5%;
  right: 5%;
  bottom: 5%;
  left: 5%;
  z-index: 75;
  text-align: center;
  display: flex;
  justify-content: center;
  align-items: center;
}

.JesterBox div:before {
  content: '';
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 74;
  background-color: rgba(0, 0, 0, 0);
  transition-property: opacity;
  transition-duration: 0.4s ;
}

.JesterBox div img {
  position: relative;
  z-index: 77;
  max-width: 100%;
  max-height: 100%;
  opacity: 0;
  transition-property: opacity;
  transition-duration: 0.4s ;
}

.JesterBox div:target { visibility: visible; }

.JesterBox div:target:before { background-color: rgba(0, 0, 0, 0.7); }

.JesterBox div:target img {
  margin-left: 0px;
  opacity: 1;
}


